import React, { useState } from "react";


const CreateAuthor = () => {
    const[name, setName] = useState("");



    return <div>
        <h2>Add a new author:</h2>
        <form className="background-black">
            <div>
                <label>Name: </label>
                <br></br>
                <input 
                type="text" 
                value={name} 
                onChange={(e)=>setName(e.target.value)}
                />
            </div>
        </form>
    </div>
};

export default CreateAuthor;
