import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Details = () => {
  const [oneAuthor, setOneAuthor] = useState([]);
  const [name, setName] = useState({});
  const [description, setDescription] = useState({});

  const { id } = useParams();

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/authors/${id}`)
      .then((res) => {
        const oneAuthor = res.data;
        setOneAuthor(oneAuthor);
        setName(oneAuthor.name);
        setDescription(oneAuthor.description);
      })
      .catch((err) => console.log(err));
  }, []);

  {
    oneAuthor.map((oneAuthor, idx) => {
      return (
        <div id="detailsPageCentering">
          <p>Name: {oneAuthor.name}</p>
          <p>Description: {oneAuthor.description}</p>
        </div>
      );
    });
  }
};

export default Details;
