import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { oneAuthor } from "../../../server/controllers/authors.controller";

const Details = () => {
  const [name, setName] = useState({});
  const [description, setDescription] = useState({});

  const { id } = useParams();

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/authors/${id}`)
      .then((res) => {
        const oneAuthor = res.data;
        setName(oneAuthor.name);
        setDescription(oneAuthor.description);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div id="detailsPageCentering">
      <p>Name: {oneAuthor.name}</p>
      <p>Description: {oneAuthor.description}</p>
    </div>
  );
};

export default Details;
